package com.hidear.law.modular.user.controller;import com.hidear.law.modular.user.model.User;import com.hidear.law.modular.user.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.stereotype.Service;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;/** * Created by acer on 2017/7/2. */@Controllerpublic class UserController {    @Autowired    UserService userService;    @RequestMapping("/")    @ResponseBody    public User getUser(){        User user = new User();        user.setId(1);        user.setUsername("username");        userService.saveUser(user);        User user2 = userService.getUserById(user.getId());        return user2;    }    @RequestMapping("/getuers")    @ResponseBody    public List<User> getUsers(){        User user = new User();        user.setId(1);        return userService.getUserByUser(user);    }}